(function($) {
    $.fn.imagezoom = function(options) {
        var settings = {
            xzoom: 310,
            yzoom: 310,
            offset: 10,
            position: "BTR",
            preload: 1
        };
        if (options) {
            $.extend(settings, options);
        }
        var noalt = '';
        var self = this;
        $(this).bind("mouseenter",
            function(ev) {
                var imageLeft = $(this).parent().parent().offset().left;
                var imageTop = $(this).parent().parent().offset().top;
                var imageWidth = $(this).parent().parent().get(0).offsetWidth;
                var imageHeight = $(this).parent().parent().get(0).offsetHeight;
                var boxLeft = $(this).parent().parent().offset().left;
                //var boxTop = $(this).parent().parent().offset().top;
                var boxTop=0;
                var boxWidth = $(this).parent().parent().width();
                var boxHeight = $(this).parent().parent().height();
                noalt = $(this).attr("alt");
                var bigimage = $(this).attr("rel");
                $(this).attr("alt", '');
                if ($("div.zoomDiv").get().length == 0) {
                    $('#content .left .tb-pic').append("<div class='zoomDiv'><img class='bigimg' src='" + bigimage + "'/></div><div class='zoomMask'>&nbsp;</div>");
                }
                if (settings.position == "BTR") {
                    if (boxLeft + boxWidth + settings.offset + settings.xzoom > screen.width) {
                        leftpos = boxLeft - settings.offset - settings.xzoom;
                    } else {
                        leftpos =boxWidth+10;
                    }
                } else {
                    leftpos = imageLeft - settings.xzoom - settings.offset;
                    if (leftpos < 0) {
                        leftpos = imageLeft + imageWidth + settings.offset;
                    }
                }
                $("div.zoomDiv").css({
                    top: boxTop,
                    left: leftpos
                });
                $("div.zoomDiv").width(settings.xzoom);
                $("div.zoomDiv").height(settings.yzoom);
                $("div.zoomDiv").show();
                //设置显示图片大小

                $(this).css('cursor', 'crosshair');
                $(document).mousemove(function(e) {
                    mouse = new MouseEvent(e);
                    if (mouse.x < imageLeft || mouse.x > imageLeft + imageWidth || mouse.y < imageTop || mouse.y > imageTop + imageHeight) {
                        mouseOutImage();
                        return;
                    }
                    //var bigwidth = $(".bigimg").get(0).offsetWidth;
                    //var bigheight = $(".bigimg").get(0).offsetHeight;
                    var scaley = 'x';
                    var scalex = 'y';
                    if (isNaN(scalex) | isNaN(scaley)) {
                        //var scalex = (bigwidth / imageWidth);
                        //var scaley = (bigheight / imageHeight);
                        var scalex=3;
                        var scaley=3;
                        $("div.zoomMask").width((settings.xzoom) / scalex);
                        $("div.zoomMask").height((settings.yzoom) / scaley);
                        $("div.zoomMask").css('visibility', 'visible');
                    }
                    $('.zoomDiv .bigimg').css({'width':($('.zoomDiv').width()/$('.zoomMask').width())*100+'%','height':($('.zoomDiv').height()/$('.zoomMask').height())*100+'%'});
                    xpos = (mouse.x-imageLeft) - $("div.zoomMask").width() / 2;
                    ypos = (mouse.y-imageTop) - $("div.zoomMask").height() / 2;
                    xposs = mouse.x - $("div.zoomMask").width() / 2 - imageLeft;
                    yposs = mouse.y - $("div.zoomMask").height() / 2 - imageTop;
                    //xpos = ((mouse.x-imageLeft)- $("div.zoomMask").width() / 2 < imageLeft) ? imageLeft: (mouse.x + $("div.zoomMask").width() / 2 > imageWidth + imageLeft) ? (imageWidth + imageLeft - $("div.zoomMask").width()) : xpos;
                    //ypos = (mouse.y - $("div.zoomMask").height() / 2 < imageTop) ? imageTop: (mouse.y + $("div.zoomMask").height() / 2 > imageHeight + imageTop) ? (imageHeight + imageTop - $("div.zoomMask").height()) : ypos;
                    xpos=((mouse.x-imageLeft) - $("div.zoomMask").width() / 2) < 0 ? 0 : xpos;
                    if((mouse.x + $("div.zoomMask").width() / 2 > imageWidth + imageLeft)){
                        xpos=imageWidth-$("div.zoomMask").width();
                    }
                    ypos=((mouse.y-imageTop) - $("div.zoomMask").height() / 2) < 0 ? 0 : ypos;
                    if((mouse.y + $("div.zoomMask").height() / 2 > imageHeight + imageTop)){
                        ypos=imageHeight-$("div.zoomMask").height();
                    }
                    $("div.zoomMask").css({
                        top: ypos,
                        left: xpos
                    });
                    //$("div .zoomDiv img").get(0).style.left = xposs * scalex;
                    //$("div .zoomDiv img").get(0).style.top = yposs * scaley;
                    $("div .zoomDiv img").css({'left':-(xpos * scalex) , 'top':-(ypos * scaley)});
                });
            });
        //$(this).on('mouseout',function(){
        //    mouseOutImage();
        //});
        function mouseOutImage() {
            $(self).attr("alt", noalt);
            $(document.body).unbind("mousemove");
            $("div.zoomMask").remove();
            $("div.zoomDiv").remove();
        }
        count = 0;
        if (settings.preload) {
            $('body').append("<div style='display:none;' class='jqPreload" + count + "'></div>");
            $(this).each(function() {
                var imagetopreload = $(this).attr("rel");
                var content = jQuery('div.jqPreload' + count + '').html();
                jQuery('div.jqPreload' + count + '').html(content + '<img src=\"' + imagetopreload + '\">');
            });
        }
    }
})(jQuery);
function MouseEvent(e) {
    this.x = e.pageX;
    this.y = e.pageY;
}